import React, { useState } from 'react'
import '../assets/css/login.css'
import { useHistory } from 'react-router-dom'
import {useAuth, login, logout} from "../auth/index"


const Login = (props) => {
  // const [csrf, setCsrf] = useState(null);
  const [user, setUser] = useState(props.user)

  const [logged] = useAuth()

  const history = useHistory();

  const loginSubmit = e => {
    e.preventDefault();
    fetch('/api/login', {
      method: 'post',
      url: '/login',
      body: JSON.stringify(user),
      headers: {
        'content-type': 'application/json'
      }
    })
    .then(r => r.json())
    .then(token => {
    if (token.access_token){
      login(token)
      console.log(token)  
      history.push('/')        
    }
    else {
      alert("Please type in correct username/password")
    }
  })
    // .then(res => {
    //   if (res.status === 200) {
    //     console.log('User Validated, redirecting to home')
    //     history.push('/')
    //   }
    //   console.log(res)
    //   return res.json
    // })
    // .then(json => {
    //   setUser(json.user) 
    //   console.log(json)
    // })
    }


  return (
        <div className="login text-center">  
          <main className="form-signin">
          {!logged? <form autoComplete="on">
              {/* <img className="mb-4" src="/docs/5.0/assets/brand/bootstrap-logo.svg" alt="" width="72" height="57" /> */}
              <h1 className="h3 mb-3 fw-normal">Please sign in</h1>
              <label htmlFor="inputEmail" className="visually-hidden">Email address</label>
              <input 
              type="email" 
              id="inputEmail" 
              className="form-control" 
              placeholder="Email address" 
              onChange={e => setUser({ ...user, email: e.target.value.toLowerCase().trim()})}
              required autoFocus
              />
              <label htmlFor="inputPassword" className="visually-hidden">Password</label>
              <input 
              type="password" 
              id="inputPassword" 
              className="form-control" 
              placeholder="Password" 
              onChange={e => setUser({ ...user, password: e.target.value })}
              required
              />
                  {/* <div className="checkbox mb-3">
                    <label>
                      <input type="checkbox" value="remember-me"/> Remember me
                    </label>
                  </div> */}
              <button className="w-100 btn btn-lg btn-primary" type="submit" onClick={loginSubmit}>Sign in</button>
              {/* <p className="mt-5 mb-3 text-muted">&copy; 2017–2021</p> */}
              <a href="/forgot">Forgot password</a>
            </form>
            :<button className="w-100 btn btn-lg btn-primary" onClick={() => logout()}>Logout</button>
          }
          </main>
        </div>
  )
}

export default Login
